package week1.day1;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			
		int age = 30;
		float height = 5.5f;
		double salary = 3650.2211554455;
		char ch = 'J';
		String name = "Sree";
		long phnenum = 123456789123l;
		boolean isTrue = true;
		
		
		System.out.println("my age is "+age+"\n"+"My height is "+height); //+=concatenation
		System.out.println("My height is "+height);
		System.out.println("My salary is "+salary);
		System.out.print("My name is "+ch+" ");
		System.out.println(name);
		System.out.println("phone number : "+phnenum);
		System.out.println("True/False: "+isTrue);

	}

}
