package week1.day1;

public class OddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			
		int number = 0;
		
		if (number %2 == 0) {
			System.out.println("given number "+number+" is even");
		}else {
			System.out.println("given number "+number+" is odd");

		}
		
	}

}
